import React, { useState } from 'react';
import './Schedule.css';

const Schedule = () => {
  const [selectedTeam, setSelectedTeam] = useState('This week');

  // Generate weeks of data (past and future)
  const generateWeeks = (startDate, numWeeks) => {
    const weeks = [];
    const currentDate = new Date(startDate);

    for (let i = 0; i < numWeeks; i++) {
      const week = [];
      const weekStart = new Date(currentDate);
      weekStart.setDate(currentDate.getDate() - (currentDate.getDay() - 1)); // Start from Monday

      for (let j = 0; j < 7; j++) {
        const day = new Date(weekStart);
        day.setDate(weekStart.getDate() + j);
        week.push(day);
      }
      weeks.push(week);
      currentDate.setDate(currentDate.getDate() + 7);
    }

    return weeks;
  };

  // Generate 52 weeks (1 year) of data
  const startDate = new Date();
  startDate.setDate(startDate.getDate() - 26 * 7); // Start 26 weeks ago
  const allWeeks = generateWeeks(startDate, 52);
  const [currentWeekIndex, setCurrentWeekIndex] = useState(26); // Start at current week

  // Sample data matching the Float interface
  const teamMembers = [
    {
      id: 1,
      initials: 'RR',
      name: 'Recekesavan R',
      role: 'Team Lead',
      hours: '32h',
      color: '#4CAF50'
    },
    {
      id: 2,
      initials: 'S',
      name: 'Sastha',
      role: 'Recekesavan Team',
      hours: '16.5h',
      color: '#FF9800'
    },
    {
      id: 3,
      initials: 'J',
      name: 'Jakhir',
      role: 'Team Lead',
      hours: '16h',
      color: '#2196F3'
    },
    {
      id: 4,
      initials: 'I',
      name: 'Ibrahim',
      role: 'Recekesavan Team',
      hours: '16h',
      color: '#9C27B0'
    },
    {
      id: 5,
      initials: 'RK',
      name: 'Ram kumar',
      role: 'Team Lead',
      hours: '40h',
      color: '#F44336'
    }
  ];

  const tasks = [
    {
      id: 1,
      personId: 1,
      weekIndex: 26,
      dayIndex: 2, // Wednesday
      title: 'Submittal d Auburn Vist',
      hours: '4h',
      color: '#F44336'
    },
    {
      id: 2,
      personId: 1,
      weekIndex: 26,
      dayIndex: 2,
      title: 'No Task Dakota Affo',
      hours: '4h',
      color: '#4CAF50'
    },
    {
      id: 3,
      personId: 2,
      weekIndex: 26,
      dayIndex: 0,
      title: 'Level 3 - Ai Sand Canyc EDC',
      hours: '7.5h',
      color: '#FFE0B2'
    },
    {
      id: 4,
      personId: 2,
      weekIndex: 26,
      dayIndex: 1,
      title: 'Level 3 - Ai Sand Canyc EDC',
      hours: '8h',
      color: '#FFE0B2'
    },
    {
      id: 5,
      personId: 2,
      weekIndex: 26,
      dayIndex: 2,
      title: 'Level 3 - Ai Sand Canyc EDC',
      hours: '8h',
      color: '#FFE0B2'
    },
    {
      id: 6,
      personId: 3,
      weekIndex: 26,
      dayIndex: 3,
      title: 'No Task Sand Canyon Plaza EDC',
      hours: '8h',
      color: '#FFE0B2'
    },
    {
      id: 7,
      personId: 3,
      weekIndex: 26,
      dayIndex: 4,
      title: 'No Task Sand Canyc EDC',
      hours: '8h',
      color: '#FFE0B2'
    },
    {
      id: 8,
      personId: 4,
      weekIndex: 26,
      dayIndex: 0,
      title: 'Level 3 - Ai Sand Canyc EDC',
      hours: '8h',
      color: '#FFE0B2'
    },
    {
      id: 9,
      personId: 4,
      weekIndex: 26,
      dayIndex: 1,
      title: 'Level 3 - Ai Sand Canyc EDC',
      hours: '8h',
      color: '#FFE0B2'
    },
    {
      id: 10,
      personId: 4,
      weekIndex: 26,
      dayIndex: 2,
      title: 'Level 3 - Ai Sand Canyc EDC',
      hours: '8h',
      color: '#FFE0B2'
    }
  ];

  const getTasksForPersonAndDay = (personId, weekIndex, dayIndex) => {
    return tasks.filter(task => 
      task.personId === personId && 
      task.weekIndex === weekIndex && 
      task.dayIndex === dayIndex
    );
  };

  const formatDay = (date) => {
    const days = ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'];
    const dayName = days[date.getDay() === 0 ? 6 : date.getDay() - 1];
    const dayNumber = date.getDate();
    return `${dayName} ${dayNumber}`;
  };

  const formatMonthRange = () => {
    const currentWeek = allWeeks[currentWeekIndex];
    const firstDay = currentWeek[0];
    const lastDay = currentWeek[6];

    const firstMonth = firstDay.toLocaleDateString('en-US', { month: 'short' });
    const lastMonth = lastDay.toLocaleDateString('en-US', { month: 'short' });

    if (firstMonth === lastMonth) {
      return firstMonth;
    }
    return `${firstMonth} - ${lastMonth}`;
  };

  const getCurrentDay = () => {
    const today = new Date();
    const currentWeek = allWeeks[currentWeekIndex];
    const todayIndex = currentWeek.findIndex(day => 
      day.toDateString() === today.toDateString()
    );

    if (todayIndex !== -1) {
      return formatDay(currentWeek[todayIndex]);
    }
    return null;
  };

  const navigateWeek = (direction) => {
    const newIndex = currentWeekIndex + direction;
    if (newIndex >= 0 && newIndex < allWeeks.length) {
      setCurrentWeekIndex(newIndex);
    }
  };

  const goToToday = () => {
    setCurrentWeekIndex(26); // Current week index
  };

  const currentWeek = allWeeks[currentWeekIndex];
  const currentDay = getCurrentDay();

  return (
    <div className="schedule-container">
      {/* Header */}
      <div className="schedule-header">
        <div className="header-left">
          <div className="team-selector">
            <span className="team-icon">üë•</span>
            <span className="team-dropdown">{selectedTeam} ‚ñº</span>
          </div>
          <span className="total-hours">816.5h</span>
        </div>

        <div className="header-center">
          <h2>{formatMonthRange()}</h2>
          {currentDay && <span className="current-day">{currentDay}</span>}
        </div>

        <div className="header-right">
          <button className="nav-btn" onClick={() => navigateWeek(-1)}>‚Üê</button>
          <button className="nav-btn" onClick={() => navigateWeek(1)}>‚Üí</button>
          <button className="today-btn" onClick={goToToday}>Today</button>
          <select className="view-selector">
            <option>Weeks</option>
          </select>
          <button className="icon-btn">‚öôÔ∏è</button>
          <button className="icon-btn">üì§</button>
          <button className="icon-btn">+</button>
        </div>
      </div>

      {/* Schedule Grid */}
      <div className="schedule-grid">
        {/* Days Header */}
        <div className="days-header">
          <div className="person-header"></div>
          {currentWeek.map((day, index) => (
            <div key={index} className="day-header">
              {formatDay(day)}
            </div>
          ))}
        </div>

        {/* People Rows */}
        {teamMembers.map((person) => (
          <div key={person.id} className="person-row">
            {/* Person Info */}
            <div className="person-info">
              <div className="person-avatar" style={{ backgroundColor: person.color }}>
                {person.initials}
              </div>
              <div className="person-details">
                <div className="person-name">{person.name}</div>
                <div className="person-role">{person.role}</div>
              </div>
              <div className="person-hours">{person.hours}</div>
            </div>

            {/* Task Blocks */}
            {currentWeek.map((day, dayIndex) => (
              <div key={dayIndex} className="day-cell">
                {getTasksForPersonAndDay(person.id, currentWeekIndex, dayIndex).map((task) => (
                  <div 
                    key={task.id} 
                    className="task-block"
                    style={{ backgroundColor: task.color }}
                  >
                    <div className="task-title">{task.title}</div>
                    <div className="task-hours">{task.hours}</div>
                  </div>
                ))}
              </div>
            ))}
          </div>
        ))}
      </div>

      {/* Week Navigation Info */}
      <div className="week-info">
        Week {currentWeekIndex + 1} of {allWeeks.length} 
        ({currentWeekIndex - 26 > 0 ? `+${currentWeekIndex - 26} weeks ahead` : 
          currentWeekIndex - 26 < 0 ? `${Math.abs(currentWeekIndex - 26)} weeks ago` : 'Current week'})
      </div>

      {/* Trial Notice */}
      <div className="trial-notice">
        15 days left on your trial. Need help?
      </div>
    </div>
  );
};

export default Schedule;
