    return `${firstMonth} - ${lastMonth}`;
  };

  const getCurrentDay = () => {
    const today = new Date();
    const currentWeek = allWeeks[currentWeekIndex];
    const todayIndex = currentWeek.findIndex(day => 
      day.toDateString() === today.toDateString()
    );

    if (todayIndex !== -1) {
      return formatDay(currentWeek[todayIndex]);
    }
    return null;
  };

  const navigateWeek = (direction) => {
    const newIndex = currentWeekIndex + direction;
    if (newIndex >= 0 && newIndex < allWeeks.length) {
      setCurrentWeekIndex(newIndex);
    }
  };

  const goToToday = () => {
    setCurrentWeekIndex(26); // Current week index
  };

  const handleTaskClick = (task) => {
    setSelectedTask(task);
    setShowModal(true);
  };

  const handleEmptyCellClick = (personId, weekIndex, dayIndex, day) => {
    const newTask = {
      id: Date.now(), // Generate unique ID
      personId: personId,
      weekIndex: weekIndex,
      dayIndex: dayIndex,
      title: '',
      hours: '8h',
      color: '#FF6B6B',
      project: '',
      status: 'pending',
      notes: '',
      startDate: day,
      endDate: day,
      duration: 1
    };
    setSelectedTask(newTask);
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setSelectedTask(null);
  };

  const handleTaskUpdate = (updatedTask) => {
    setTasks(prevTasks => {
      let newTasks;

      // Handle delete
      if (updatedTask._delete) {
        newTasks = prevTasks.filter(task => task.id !== updatedTask.id);
      }
      // If it's a new task (no existing id), add it
      else if (!updatedTask.id || !prevTasks.find(t => t.id === updatedTask.id)) {
        newTasks = [...prevTasks, updatedTask];
      }
      // Otherwise update existing task
      else {
        newTasks = prevTasks.map(task => 
          task.id === updatedTask.id ? updatedTask : task
        );
      }

      // Save to localStorage
      localStorage.setItem('schedule-tasks', JSON.stringify(newTasks));
      return newTasks;
    });
  };

  const currentWeek = allWeeks[currentWeekIndex];
  const currentDay = getCurrentDay();

  return (
    <div className="schedule-container">
      {/* Header */}
      <div className="schedule-header">
        <div className="header-left">
          <button className="add-person-btn" onClick={() => setShowPersonModal(true)}>
            <span className="person-icon">üë§</span>
            <span className="plus-icon">+</span>
            <span className="btn-text">Add person</span>
          </button>
        </div>

        <div className="header-center">
          <h2>{formatMonthRange()}</h2>
          {currentDay && <span className="current-day">{currentDay}</span>}
        </div>

        <div className="header-right">
          <button className="nav-btn" onClick={() => navigateWeek(-1)}>‚Üê</button>
          <button className="nav-btn" onClick={() => navigateWeek(1)}>‚Üí</button>
          <button className="today-btn" onClick={goToToday}>Today</button>
        </div>
      </div>

      {/* Schedule Grid */}
      <div className="schedule-grid">
        {/* Days Header */}
        <div className="days-header">
          {currentWeek.map((day, index) => (
            <div key={index} className="day-header">
              {formatDay(day)}
            </div>
          ))}
        </div>

        {/* People Rows */}
        {teamMembers.map((person) => (
          <div key={person.id} className="person-row">
            {/* Task Blocks */}
            {currentWeek.map((day, dayIndex) => (
              <div 
                key={dayIndex} 
                className="day-cell"
                onClick={() => handleEmptyCellClick(person.id, currentWeekIndex, dayIndex, day)}
              >
                {getTasksForPersonAndDay(person.id, currentWeekIndex, dayIndex).map((task) => (
                  <div 
                    key={task.id} 
                    className="task-block"
                    style={{ backgroundColor: task.color }}
                    onClick={(e) => {
                      e.stopPropagation();
                      handleTaskClick(task);
                    }}
                  >
                    <div className="task-title">{task.title}</div>
                    <div className="task-hours">{task.hours}</div>
                  </div>
                ))}
              </div>
            ))}
          </div>
        ))}
      </div>

      {/* Week Navigation Info */}
      <div className="week-info">
        Week {currentWeekIndex + 1} of {allWeeks.length} 
        ({currentWeekIndex - 26 > 0 ? `+${currentWeekIndex - 26} weeks ahead` : 
          currentWeekIndex - 26 < 0 ? `${Math.abs(currentWeekIndex - 26)} weeks ago` : 'Current week'})
      </div>



      {/* Allocation Modal */}
      {showModal && selectedTask && (
        <AllocationModal 
          task={selectedTask}
          person={teamMembers.find(p => p.id === selectedTask.personId)}
          onClose={closeModal}
          onUpdate={handleTaskUpdate}
        />
      )}
    </div>
  );
};

export default Schedule;
