import React, { useState } from 'react';
import './AllocationModal.css';

const AllocationModal = ({ task, person, onClose }) => {
  const [hours, setHours] = useState(task.hours.replace('h', ''));
  const [status, setStatus] = useState(task.status);
  const [notes, setNotes] = useState(task.notes);
  const [project, setProject] = useState(task.project || '');
  const [taskTitle, setTaskTitle] = useState(task.title || '');
  const [currentTask, setCurrentTask] = useState(task);

  const isNewTask = !task.id || task.id === Date.now();

  const handleDateClick = (pickerId) => {
    const picker = document.getElementById(pickerId);
    if (picker) {
      picker.showPicker();
    }
  };

  const handleUpdate = () => {
    // Here you would typically update the task data
    console.log('Updating task:', { ...task, hours: `${hours}h`, status, notes });
    onClose();
  };

  const handleCancel = () => {
    onClose();
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        {/* Tabs */}
        <div className="modal-tabs">
          <button className="tab-btn active">Allocation</button>
          <button className="tab-btn">Time off</button>
          <button className="tab-btn">Status</button>
        </div>

        <div className="modal-body">
          {/* Hours Section */}
          <div className="form-section">
            <div className="hours-row">
              <div className="hours-input-group">
                <label>Hours</label>
                <div className="hours-input">
                  <input 
                    type="text" 
                    value={hours} 
                    onChange={(e) => setHours(e.target.value)}
                    className="hours-input-field"
                  />
                  <span className="hours-unit">h/day</span>
                </div>
              </div>
              <div className="total-hours">
                <label>Total hours</label>
                <input 
                  type="text" 
                  value={hours} 
                  onChange={(e) => setHours(e.target.value)}
                  className="total-hours-input"
                />
              </div>
            </div>
            <div className="specific-time-link">
              <a href="#specific-time">Specific time</a>
            </div>
            <div className="duration-info">
              Duration: <input 
                type="text" 
                value={currentTask.duration} 
                onChange={(e) => setCurrentTask({...currentTask, duration: e.target.value})}
                className="duration-input"
              /> working day
            </div>
            <div className="date-range">
              <div className="date-input-container">
                <input 
                  type="text" 
                  value={currentTask.startDate ? currentTask.startDate.toDateString() : ''} 
                  onChange={(e) => setCurrentTask({...currentTask, startDate: new Date(e.target.value)})}
                  className="date-input"
                  placeholder="Select date"
                  readOnly
                />
                <input 
                  type="date" 
                  value={currentTask.startDate ? currentTask.startDate.toISOString().split('T')[0] : ''} 
                  onChange={(e) => setCurrentTask({...currentTask, startDate: new Date(e.target.value)})}
                  className="date-picker-hidden"
                  id="start-date-picker"
                />
              </div>
              <span className="date-arrow">→</span>
              <div className="date-input-container">
                <input 
                  type="text" 
                  value={currentTask.endDate ? currentTask.endDate.toDateString() : ''} 
                  onChange={(e) => setCurrentTask({...currentTask, endDate: new Date(e.target.value)})}
                  className="date-input"
                  placeholder="Select date"
                  readOnly
                />
                <input 
                  type="date" 
                  value={currentTask.endDate ? currentTask.endDate.toISOString().split('T')[0] : ''} 
                  onChange={(e) => setCurrentTask({...currentTask, endDate: new Date(e.target.value)})}
                  className="date-picker-hidden"
                  id="end-date-picker"
                />
              </div>
            </div>
            <div className="repeat-dropdown">
              <input 
                type="text" 
                value="Doesn't repeat" 
                onChange={(e) => {}}
                className="repeat-input"
              />
              <span className="dropdown-arrow">▼</span>
            </div>
          </div>

          {/* Project Section */}
          <div className="form-section">
            <label>Project</label>
            <div className="project-input">
              <input 
                type="text"
                value={project}
                onChange={(e) => setProject(e.target.value)}
                placeholder=""
                className="project-input-field"
              />
              <span className="dropdown-arrow">▼</span>
            </div>
          </div>

          {/* Notes Section */}
          <div className="form-section">
            <label>Notes</label>
            <textarea 
              value={notes}
              onChange={(e) => setNotes(e.target.value)}
              placeholder="Add details specific to this allocation"
              className="notes-textarea"
            />
          </div>

          {/* Assigned to Section */}
          <div className="form-section">
            <label>Assigned to</label>
            <div className="assigned-tag">
              <span className="assigned-name">{person.name}</span>
              <button className="remove-btn" onClick={() => {}}>×</button>
            </div>
          </div>
        </div>

        {/* Action Buttons */}
        <div className="modal-footer">
          <div className="action-buttons">
            <button className="create-btn" onClick={handleUpdate}>
              Create allocation
            </button>
            <button className="cancel-btn" onClick={handleCancel}>
              Cancel
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AllocationModal;
