import React, { useState, useEffect } from 'react';
import './PersonModal.css';

const PersonModal = ({ person, onClose, onSave }) => {
  const [activeTab, setActiveTab] = useState('info');
  const [isClosing, setIsClosing] = useState(false);
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);
  const [formData, setFormData] = useState({
    name: person?.name || '',
    email: person?.email || '',
    role: person?.role || 'Member',
    costRate: person?.costRate || 'Rs 0',
    billRate: person?.billRate || 'Rs 0',
    department: person?.department || 'No department',
    tags: person?.tags || '',
    type: person?.type || 'Employee'
  });

  const [showRoleDropdown, setShowRoleDropdown] = useState(false);
  const [showDepartmentDropdown, setShowDepartmentDropdown] = useState(false);
  const [showTagsDropdown, setShowTagsDropdown] = useState(false);
  const [showTypeDropdown, setShowTypeDropdown] = useState(false);

  const roleOptions = ['Member', 'Team Lead'];
  const departmentOptions = ['Gowtham Team', 'Mohanapriya Team', 'Recekesavan Team', 'Wood'];
  const tagOptions = ['No tags', 'Team-1'];
  const typeOptions = ['Employee', 'Contractor', 'Placeholder'];

  const handleInputChange = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleClose = () => {
    setIsClosing(true);
    setTimeout(() => {
      onClose();
    }, 200);
  };

  const handleSave = () => {
    onSave(formData);
    handleClose();
  };

  // Cleanup effect to ensure modal is properly closed
  useEffect(() => {
    const handleEscape = (e) => {
      if (e.key === 'Escape') {
        handleClose();
      }
    };

    document.addEventListener('keydown', handleEscape);
    return () => {
      document.removeEventListener('keydown', handleEscape);
    };
  }, []);

  return (
    <div className={`person-modal-overlay ${isClosing ? 'closing' : ''}`} onClick={handleClose}>
      <div className="person-modal-content" onClick={(e) => e.stopPropagation()}>
        {/* Header */}
        <div className="person-header">
          <div className="person-info">
            <div className="person-name">
              <input
                type="text"
                value={formData.name}
                onChange={(e) => handleInputChange('name', e.target.value)}
                placeholder="Name"
                className="name-input"
              />
            </div>
            <div className="person-email">
              <input
                type="email"
                value={formData.email}
                onChange={(e) => handleInputChange('email', e.target.value)}
                placeholder="Email"
                className="email-input"
              />
            </div>
          </div>
          <div className="person-avatar">
            <div className="avatar-placeholder">
              <span>👤</span>
            </div>
          </div>
        </div>

        {/* Tabs */}
        <div className="person-tabs">
          <button 
            className={`tab-btn ${activeTab === 'info' ? 'active' : ''}`}
            onClick={() => setActiveTab('info')}
          >
            Info
          </button>
          <button 
            className={`tab-btn ${activeTab === 'access' ? 'active' : ''}`}
            onClick={() => setActiveTab('access')}
          >
            Access
          </button>
          <button 
            className={`tab-btn ${activeTab === 'availability' ? 'active' : ''}`}
            onClick={() => setActiveTab('availability')}
          >
            Availability
          </button>
          <button 
            className={`tab-btn ${activeTab === 'projects' ? 'active' : ''}`}
            onClick={() => setActiveTab('projects')}
          >
            Projects
          </button>
        </div>

        {/* Tab Content */}
        <div className="person-body">
          {activeTab === 'info' && (
            <div className="info-tab">
              <div className="form-section">
                <label>Role</label>
                <div className="dropdown-input">
                  <input
                    type="text"
                    value={formData.role}
                    onChange={(e) => handleInputChange('role', e.target.value)}
                    placeholder="Select role"
                    className="dropdown-field"
                    readOnly
                    onClick={() => setShowRoleDropdown(!showRoleDropdown)}
                  />
                  <span className="dropdown-arrow">▼</span>
                  {showRoleDropdown && (
                    <div className="role-dropdown-menu">
                      {roleOptions.map(option => (
                        <button
                          key={option}
                          className="role-option"
                          onClick={() => {
                            handleInputChange('role', option);
                            setShowRoleDropdown(false);
                          }}
                        >
                          {option}
                        </button>
                      ))}
                    </div>
                  )}
                </div>
              </div>

              <div className="form-section">
                <label>Cost rate</label>
                <div className="rate-input">
                  <input
                    type="text"
                    value={formData.costRate}
                    onChange={(e) => handleInputChange('costRate', e.target.value)}
                    className="rate-field"
                  />
                  <span className="rate-suffix">/hr</span>
                </div>
              </div>

              <div className="form-section">
                <label>Bill rate</label>
                <div className="rate-input">
                  <input
                    type="text"
                    value={formData.billRate}
                    onChange={(e) => handleInputChange('billRate', e.target.value)}
                    className="rate-field"
                  />
                  <span className="rate-suffix">/hr</span>
                </div>
              </div>

              <div className="form-section">
                <label>Department</label>
                <div className="dropdown-input">
                  <input
                    type="text"
                    value={formData.department}
                    onChange={(e) => handleInputChange('department', e.target.value)}
                    placeholder="No department"
                    className="dropdown-field"
                    readOnly
                    onClick={() => setShowDepartmentDropdown(!showDepartmentDropdown)}
                  />
                  <span className="dropdown-arrow">▼</span>
                  {showDepartmentDropdown && (
                    <div className="department-dropdown-menu">
                      {departmentOptions.map(option => (
                        <button
                          key={option}
                          className="department-option"
                          onClick={() => {
                            handleInputChange('department', option);
                            setShowDepartmentDropdown(false);
                          }}
                        >
                          {option}
                        </button>
                      ))}
                    </div>
                  )}
                </div>
              </div>

              <div className="form-section">
                <label>Tags</label>
                <div className="dropdown-input">
                  <input
                    type="text"
                    value={formData.tags}
                    onChange={(e) => handleInputChange('tags', e.target.value)}
                    placeholder="No tags"
                    className="dropdown-field"
                    readOnly
                    onClick={() => setShowTagsDropdown(!showTagsDropdown)}
                  />
                  <span className="dropdown-arrow">▼</span>
                  {showTagsDropdown && (
                    <div className="tags-dropdown-menu">
                      {tagOptions.map(option => (
                        <button
                          key={option}
                          className="tags-option"
                          onClick={() => {
                            handleInputChange('tags', option);
                            setShowTagsDropdown(false);
                          }}
                        >
                          {option}
                        </button>
                      ))}
                    </div>
                  )}
                </div>
              </div>

              <div className="form-section">
                <label>Type</label>
                <div className="dropdown-input">
                  <input
                    type="text"
                    value={formData.type}
                    onChange={(e) => handleInputChange('type', e.target.value)}
                    className="dropdown-field"
                    readOnly
                    onClick={() => setShowTypeDropdown(!showTypeDropdown)}
                  />
                  <span className="dropdown-arrow">▼</span>
                  {showTypeDropdown && (
                    <div className="type-dropdown-menu">
                      {typeOptions.map(option => (
                        <button
                          key={option}
                          className={`type-option ${formData.type === option ? 'selected' : ''}`}
                          onClick={() => {
                            handleInputChange('type', option);
                            setShowTypeDropdown(false);
                          }}
                        >
                          {option}
                        </button>
                      ))}
                    </div>
                  )}
                </div>
              </div>
            </div>
          )}

          {activeTab === 'access' && (
            <div className="access-tab">
              <p>Access settings will be implemented here.</p>
            </div>
          )}

          {activeTab === 'availability' && (
            <div className="availability-tab">
              <p>Availability settings will be implemented here.</p>
            </div>
          )}

          {activeTab === 'projects' && (
            <div className="projects-tab">
              <p>Project assignments will be implemented here.</p>
            </div>
          )}
        </div>

        {/* Action Buttons */}
        <div className="person-footer">
          <button className="add-person-btn" onClick={handleSave}>
            {person ? 'Update person' : 'Add person'}
          </button>
          <button className="cancel-btn" onClick={handleClose}>
            Cancel
          </button>
          {person && (
            <button className="delete-btn" onClick={() => setShowDeleteConfirm(true)}>
              Delete
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default PersonModal;
