      role: 'Team Lead',
      hours: '40h',
      color: '#F44336'
    }
  ];

  const tasks = [
    {
      id: 1,
      personId: 1,
      weekIndex: 26,
      dayIndex: 2, // Wednesday
      title: 'Submittal d Auburn Vist',
      hours: '4h',
      color: '#F44336',
