    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setSelectedTask(null);
  };

  const currentWeek = allWeeks[currentWeekIndex];
  const currentDay = getCurrentDay();

  return (
    <div className="schedule-container">


      {/* Schedule Grid */}
      <div className="schedule-grid">
        {/* People Rows */}
        {teamMembers.map((person) => (
          <div key={person.id} className="person-row">
            {/* Person Info */}
            <div className="person-info">
              <div className="person-avatar" style={{ backgroundColor: person.color }}>
                {person.initials}
              </div>
              <div className="person-details">
                <div className="person-name">{person.name}</div>
                <div className="person-role">{person.role}</div>
              </div>
              <div className="person-hours">{person.hours}</div>
            </div>

            {/* Task Blocks */}
            {currentWeek.map((day, dayIndex) => (
              <div key={dayIndex} className="day-cell">
                {getTasksForPersonAndDay(person.id, currentWeekIndex, dayIndex).map((task) => (
                  <div 
                    key={task.id} 
                    className="task-block"
                    style={{ backgroundColor: task.color }}
                    onClick={() => handleTaskClick(task)}
                  >
                    <div className="task-title">{task.title}</div>
                    <div className="task-hours">{task.hours}</div>
                  </div>
                ))}
              </div>
            ))}
          </div>
        ))}
      </div>



      {/* Allocation Modal */}
      {showModal && selectedTask && (
        <AllocationModal 
          task={selectedTask}
          person={teamMembers.find(p => p.id === selectedTask.personId)}
          onClose={closeModal}
        />
      )}
    </div>
  );
};

export default Schedule;
