                {getTasksForPersonAndDay(person.id, currentWeekIndex, dayIndex).map((task) => (
                  <div 
                    key={task.id} 
                    className="task-block"
                    style={{ backgroundColor: task.color }}
                    onClick={(e) => {
                      e.stopPropagation();
                      handleTaskClick(task);
                    }}
