      <div className="week-info">
        Week {currentWeekIndex + 1} of {allWeeks.length} 
        ({currentWeekIndex - 26 > 0 ? `+${currentWeekIndex - 26} weeks ahead` : 
          currentWeekIndex - 26 < 0 ? `${Math.abs(currentWeekIndex - 26)} weeks ago` : 'Current week'})
      </div>



      {/* Allocation Modal */}
      {showModal && selectedTask && (
        <AllocationModal 
          task={selectedTask}
          person={teamMembers.find(p => p.id === selectedTask.personId)}
          onClose={closeModal}
          onUpdate={handleTaskUpdate}
        />
      )}
    </div>
  );
